    private void jButton1ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton1ActionPerformed
        // TODO add your handling code here:
        int numerator = Integer.parseInt(numeratorInput.getText());
        int denominator = Integer.parseInt(denominatorInput.getText());
        
        String reducedFraction = reduce(numerator, denominator);
        outputLabel.setText(numerator+"/"+denominator+" in lowest terms is "+reducedFraction);
    }//GEN-LAST:event_jButton1ActionPerformed

