public static String reduce(int n, int d){
    String reducedFrac = "";
    int greatestDenom = gcd(n,d);
    int reducedNum = n/greatestDenom;
    int reducedDenom = d/greatestDenom;
    reducedFrac = reducedNum+"/"+reducedDenom;
    return reducedFrac;

}    

public static int gcd(int n, int d){
    if(d==0){
        return n;
    }
    else{
        return gcd(d,n%d);
    }
}

